var fs = require('fs');
//writeFile overwrite
fs.readFile('./Dune.txt', function(err, data){
	fs.writeFile('./test.txt', data, function(err){
		if (err) {
			console.log(err);
		}
	})
});

var fs = require('fs');
//appendFile add to the file
fs.readFile('./Dune.txt', function(err, data){
	fs.appendFile('./test.txt', data, function(err){
		if (err) {
			console.log(err);
		}
	})
});