
function ProductDTO(){};

ProductDTO.prototype.getId = function() {
	return this.id;
};
ProductDTO.prototype.getName = function() {
	return this.name;
};
ProductDTO.prototype.getDesc = function() {
	return this.desc;
};
ProductDTO.prototype.getPrice = function() {
	return this.price;
};
ProductDTO.prototype.getImage = function() {
	return this.image;
};

ProductDTO.prototype.setId = function(id) {
	this.id=id;
	return this;
};
ProductDTO.prototype.setName = function(name) {
	this.name=name;
	return this;
};
ProductDTO.prototype.setDesc = function(desc) {
	this.desc=desc;
	return this;
};
ProductDTO.prototype.setPrice = function(price) {
	this.price=price;
	return this;
};
ProductDTO.prototype.setImage = function(image) {
	this.image=image;
	return this;
};

module.exports=ProductDTO;