var fs=require('fs');

process.stdin.setEncoding('utf8');

process.stdin.on('data', function(data){
	var str=data.toLocaleLowerCase();
	var spy=str.indexOf('imie');
	if (spy!=-1) {	
		fs.appendFileSync('spy.txt', 'Keyword < IMIE > :' + data);
		}
	});

