var Monster =require('./monster');
var gollum = new Monster();
console.log(gollum);

var godzilla = new Monster(65);
console.log(godzilla.__proto__);

godzilla.on('dead', function(event){
	console.log('Godzilla is finally dead');
	console.log('he said : ' + event.message);
});

godzilla.on('continue', function(event){
	console.log('Godzilla is hurt');
	console.log('he said : ' + event.message);
	console.log('the last hit : ' + event.lastHit);
});

godzilla.hit(15);
godzilla.hit(15);
